import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { toast } from "react-toastify";

const Login = () => {
  const [password, passwordUpdate] = useState("");
  const [email, emailUpdate] = useState("");

  const usenavigate = useNavigate();

  useEffect(() => {
    const auth = localStorage.getItem("user");
    if (auth) {
      usenavigate("/homepage");
    }
    // sessionStorage.clear();
  }, []);

  const ProceedLogin = async (e) => {
    e.preventDefault();
    let obj = { email, password };
    console.warn(obj);
    if (validate()) {
      let result = await fetch("https://mine-cart-backend.vercel.app/login", {
        method: "post",
        headers: { "content-type": "application/json" },
        body: JSON.stringify(obj),
      });
      console.log("here");
      result = await result.json();
      console.warn("result", result);
      if (result.id) {
        localStorage.setItem("user", JSON.stringify(result.email));
        toast.success("Login Successful");
        usenavigate("/homepage");
      } else {
        toast.error("Enter correct credentials");
      }

      //  implement Login
      //  console.log("proceed");

      // fetch("http://localhost:3001/login/" + username)
      //   .then((res) => {
      //     return res.json();
      //   })
      //   .then((resp) => {
      //     //  console.log(resp)
      //     if (Object.keys(resp).length === 0) {
      //       toast.error("Username not found");
      //     } else {
      //       if (resp.password === password) {
      //         if (resp.email === email) {
      //           usenavigate("/homepage");
      //           localStorage.setItem("user", JSON.stringify(resp.email));
      //           toast.success("Login successful");
      //           sessionStorage.setItem("username", username);
      //         } else {
      //           toast.error("Invalid credentials");
      //         }
      //       } else {
      //         toast.error("Incorrect credentials");
      //       }
      //     }
      //   })
      //   .catch((err) => {
      //     toast.error("Login failed due to :" + err.message);
      //   });
    }
  };

  const validate = () => {
    let result = true;
    if (email === "" || email === null) {
      result = false;
      toast.warning("Please enter email");
    }
    if (password === "" || password === null) {
      result = false;
      toast.warning("Please enter password");
    }
    return result;
  };

  return (
    <div className="row">
      <div className="offset-lg-3 col-lg-6">
        <form className="container">
          <div className="card">
            <div className="card-header">
              <h2>User Login</h2>
            </div>
            <div className="card-body">
              <div className="form-group">
                <label>
                  E Mail<span className="errmsg">*</span>
                </label>
                <input
                  type="email"
                  value={email}
                  onChange={(e) => emailUpdate(e.target.value)}
                  className="form-control"
                ></input>
              </div>
              <div className="form-group">
                <label>
                  Password<span className="errmsg">*</span>
                </label>
                <input
                  type="password"
                  value={password}
                  onChange={(e) => passwordUpdate(e.target.value)}
                  className="form-control"
                ></input>
              </div>
            </div>
            <div className="card-footer">
              <button
                onClick={ProceedLogin}
                type="submit"
                className="btn btn-primary"
              >
                Login
              </button>
              <Link className="btn btn-success" to={"/registration"}>
                New User
              </Link>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Login;
